import React, { useContext } from 'react'
import { CalcContext } from '../../context/CalculatorContext';

const getButtonStyle = name => {

    const clsName = {
        '=': 'equalSign',
        '*': 'operationSigns',
        '/': 'operationSigns',
        '+': 'operationSigns',
        '-': 'operationSigns',
    }
    return clsName[name];
}

const Button = ({value, key}) => {
    
    const {calc, setCalc} = useContext(CalcContext);

    const decimalClick = () => {
        setCalc({
            ...calc,
            num:!calc.num.toString().includes('.') ? calc.num + value: calc.num,
        })
    }

    const resetClick = () => {
        setCalc({
        sign:'', num:0, res:0    
        })
    }

    const handleOptClick = () =>{
        const numberString = value.toString()
        let numberValue;

        if(numberString === '0' && calc.num === 0){
            numberValue = "0"
        }else{
            numberValue = Number(calc.num+ numberString)
        }

        setCalc({
            ...calc,
            num:numberValue
        })
    }

    const signClick = () => {
        setCalc({
            sign: value,
            res: !calc.res && calc.num ? calc.num : calc.res,
            num: 0
        })
    }

    const equalClick = () => {

        if(calc.res && calc.num){
            const math = (a, b, sign) => {
                const result = {
                    '+': (a,b) => a + b,
                    '-': (a,b) => a - b,
                    '*': (a,b) => a * b,
                    '/': (a,b) => a / b,
                }
                return result[sign](a,b);
            }
            setCalc({
                res: math(calc.res, calc.num, calc.sign),
                sign: '',
                num:0
            })
        }

    }

    const handleBtnClick = () => {
     const results = {
        '.': decimalClick,
        'C':resetClick,
        '+':signClick,
        '-':signClick,
        '*':signClick,
        '/':signClick,
        '=':equalClick,
     }   

     if(results[value]){
         return results[value]()
     }
     else{
        return handleOptClick()
     }
    }

  return (
    <button className={`${getButtonStyle(value)} buttonValue`} onClick={handleBtnClick}>{value}</button>
  )
}

export default Button
