import React, { useContext } from 'react'
import { CalcContext } from '../../context/CalculatorContext';

const getButtonStyle = name => {

    const clsName = {
        '=': 'equalSign',
        '*': 'operationSigns',
        '/': 'operationSigns',
        '+': 'operationSigns',
        '-': 'operationSigns',
    }
    return clsName[name];
}

const Button = ({value, key}) => {
    
    const {calc, setCalc} = useContext(CalcContext);

    const decimalClick = () => {
        setCalc({
            ...calc,
            num:!calc.num.toString().includes('.') ? calc.num + value: calc.num,
        })
    }

    const resetClick = () => {
        setCalc({
        sign:'', num:0, res:0    
        })
    }

    const handleOptClick = () =>{
        const numberString = value.toString()
        let numberValue;

        if(numberString === '0' && calc.num === 0){
            numberValue = "0"
        }else{
            numberValue = Number(calc.num+ numberString)
        }

        setCalc({
            ...calc,
            num:numberValue
        })
    }

    const handleBtnClick = () => {
     const results = {
        '.': decimalClick,
        'C':resetClick
     }   

     if(results[value]){
         return results[value]()
     }
     else{
        return handleOptClick()
     }
    }

  return (
    <button className={`${getButtonStyle(value)} buttonValue`} onClick={handleBtnClick}>{value}</button>
  )
}

export default Button
